var plugin = require("./plugin");


//获得所有应用
var plugins = plugin.getAll();

console.log(plugins);

//安装测试
plugins[0].install();

var timer = function(){
	console.log(plugins[0].status);

	if(plugins[0].status !== 'ok' ){
		setTimeout(timer,1000);
	}else{
		console.log(plugins[0]);
	}
};
timer();


