var exec = require("child_process").exec;
var express = require("express");
//var async = require("async");

var port = require("./../utils/port");

var app = express();


module.exports = {
	"init" : function(){
		app.set("views", __dirname + "/view/screen");
		app.set("view engine", "jade");
				
		//静态资源
		app.use("/static",express.static(__dirname + "/view/static"));
		
		app.get("/",require("./router/index"));

		//基础页面
		app.get(/^\/(\w+)(?:\/(\w+))?(?:\/(\w+))?/,function(req,res){
			require("./router/"+req.params[0])(req,res);
		});
	},
	"start" : function(){
		
		this.init();
		
		port.pop(function(port){
			app.listen(port);
			exec("start http://localhost:"+port+"/",function(err,stdout,stderr){
				console.log('def sever start at port:' + port);
				console.log('请访问：http://localhost:' + port + '/');
			});	
		});
		

		
	}
};
